<?php/** * @author Luca & Valentina * @package Entity * @category Amministratore */class EAmministratore extends EUtente {    /**     * @access public     * costruttore della classe     */    public function __construct() {       }  /**     * Inizializza un nuovo amministratore     * @param array $parametri dati dell'admin: username,passord,cognome,nome,email,data di nascita     * @access public     */    public function inizializzaadmin($parametri = array()) {        parent::setUsername($parametri['username']);        parent::setPassword($parametri['password']);        parent::setCognome($parametri['cognome']);        parent::setNome($parametri['nome']);        parent::setEmail($parametri['email']);        parent::setdata_nascita($parametri['data_nascita']);    }}