<?php/** * @author Luca & Valentina * @package Entity * @category Carta di Credito */class ECartaDiCredito {    /**     *@access protected     */    protected $nome_acquirente;    protected $cognome_acquirente;    protected $numero_carta;    protected $data_scadenza;    protected $creditValidationValue;     /**     * @access public     * costruttore della classe     */    public function __construct() {        }    /**     * @access public     * Inizializza una nuova carta di credito     * @param array $parametri attributi di una carta di credito: nome intestatario, cognome intestatario, numero di carta, data di scadenza, CVV     */    public function inizializza($parametri=array()) {        $this->nome_acquirente=$parametri['nome_acquirente'];        $this->cognome_acquirente=$parametri['cognome_acquirente'];        $this->numero_carta=$parametri['numero_carta'];        $this->data_scadenza=$parametri['data_scadenza'];        $this->creditValidationValue=$parametri['creditValidationValue'];    }    /**     * Imposta la data di scadenza della carta di credito, può ricevere sia la data completa     * che solo mese e anno. Se inseriti entrambi la priorità è della data completa     * @param string $data_completa data completa di scadenza in formato gg/mm/aaaa     * @param int $mese mese di scadenza     * @param int $anno anno di scadenza     * @access public     */    public function setDataScadenza($data_completa, $mese=NULL, $anno=NULL) {        if ($data_completa != NULL) {            $this->data_scadenza = $data_completa;        } elseif ($mese != NULL && $anno != NULL) {            $giorno = "01";            $mese+=1;            $this->data_scadenza = $giorno . '-' . $mese . '-' . $anno;        }    }    /**     * restituisce la data di scadenza della carta di credito     * @access public     * @return string la data di scadenza     */    public function getDataScadenza() {        return $this->data_scadenza;    }      /**     * Imposta il nome dell'intestatario     * @access public     * @param string $nome nome dell'intestatario     */    public function setNomeAcquirente($nome) {        $this->nome_acquirente=$nome;    }    /**     * Restituisce il nome dell'intestatario     * @access public     * @return string il nome dell'intestatario     */    public function getNomeAcquirente() {        return $this->nome_acquirente;    }    /**     * imposta il cognome dell'intestatario     * @access public     * @param string $cognome cognome dell'intestatario     */    public function setCognomeAcquirente($cognome) {        $this->cognome_acquirente=$cognome;    }    /**     * restituisce il cognome dell'intestatario     * @access public     * @return string il cognome dell'intestatario     */    public function getCognomeAcquirente() {        return $this->cognome_acquirente;    }/**     * Imposta il numero della carta di credito     * @access public     * @param String $numero il numero della carta di credito     */    public function setNumeroCarta($numero) {        $this->numero_carta=$numero;    }       /**     * restituisce il numero della carta di credito     * @access public     * @return String il numero della carta di credito     */    public function getNumeroCarta() {        return $this->numero_carta;    }    /**     * imposta il CVV della carta di credito     * @access public     * @param mixed $codice CVV della carta di credito     */    public function setCreditValidationValue($codice) {        $this->creditValidationValue=$codice;    }    /**     * restituisce il CVV della carta di credito     * @access public     * @return mixed CVV della carta di credito     */    public function getCreditValidationValue(){        return $this->creditValidationValue;    }        }