<?php/** *  * @access public * @package Utility */class UCookie {    public function __construct() {            }    /**     * Setta un cookie.     * $cookie è la variabile con cui è identificabile il cookie mentre $value è     * il contenuto. $path è posto pari a / perchè è il percorso più ampio su cui      * ha valore il cookie, rappresenta l’intera documentRoot del webserver.     * @param string     */    public function setCookie($cookie = 'cookieCheck', $value = 'activated', $path = '/') {        setcookie($cookie, $value, 0, $path);    }    /**     * Controlla se il cookie "cookieCheck" è settato.     * @return bool     */    public function checkCookie() {        if (isset($_COOKIE['cookieCheck'])) {            return true;        } else {            return false;        }    }   /**    * controlla se la sessione è attiva    * @return boolean    */    public function check_sessione_attiva(){        if(isset($_COOKIE['PHPSESSID']))           return false;        else           return true;            }    /**     * Elimina il cookie.     * @param string      */    public function eliminaCookie($cookie, $posizione = '/') {        setcookie($cookie, "", time() - 999999999999999, $posizione);    }    /** * restituisce il cookie * @param type $var * @return boolean */    public function getCookie($var){        if (isset($_COOKIE[$var])){            return $_COOKIE[$var];        }        else return FALSE;    }}