<?php/** * @author Luca & Valentina * @package Utility */class USingleton {    /** * @access public * @var type  */    public static $instances = array();    /**     * Il costruttore è privato perché non vogliamo che venga realizzata un'istanza     * della classe USingleton.     */    private function __construct(){            }    /**     * Crea un'istanza della classe '$var'. Si usa quando non si vogliono duplicati di     * questa istanza.     * @param string $var     * @return mixed Ritorna un'istanza della classe con nome uguale alla stringa inserita     */    public static function getInstaces($var){        if(! isset(self::$instances[$var])){            self::$instances[$var] = new $var;        }        return self::$instances[$var];    }}